package Lec1;

public class Maximum_3_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 6;
		int b = 9;
		int c = 7;
		if (a >= b && a >= c) {
			System.out.println(a);
		}
		else if (b >= a && b >= c) {
			System.out.println(b);
		} else {
			System.out.println(c);
		}
		System.out.println("Hey");

	}

}
