package Lec13;

public class String_Demo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		System.out.println("Hello" + a + b + "Bye");// Hello1020Bye
		System.out.println(a + b + "Hello" + "Bye");// 30HelloBye
		System.out.println("Hello" + (a + b) + "Bye");// Hello30Bye

	}

}
