package Lec15;

public class Shift_Opt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 7;
		System.out.println(a << 3);// left shift
		int x = -5;
		System.out.println(x << 2);
		System.out.println(a >> 2);// right shift
		System.out.println(x >> 1);
		x = -9;
		System.out.println(x >> 1);
	}

}
